import types from './cart.types';
const INITIAL_STATE = {
    cartVisible :false
}

const cartReducer = (state = INITIAL_STATE, action) =>{
    switch (action.type) {
        case types.TOGGLE_CART_VISIBILITY:
            return{
                ...state,
                cartVisible:action.payload
            }
        default:
            return state;
    }
}

export default cartReducer;
