import React from 'react';
import './cart-icon.styles.scss';
import {ReactComponent as ShoppingIcon} from '../../assets/shopping-bag.svg';
import {connect} from 'react-redux';
const CartIcon = ({clickCartIcon}) => (
    <div className='cart-icon' onClick={clickCartIcon}>
        <ShoppingIcon className='shopping-icon'/>
        {/* span doesn't cause a break before/after it comes. used to hook css to an inline element. */}
        <span className='item-count'>0</span>
    </div>
)

const mapDispatchToProps = dispatch =>({
    clickCartIcon:cartVisible => dispatch(clickCartIcon({type:'TOGGLE_CART_VISIBILITY',payload:!cartVisible}))
})

export default connect(null,mapDispatchToProps)(CartIcon);
