import {createSelector} from 'reselect';

const selectCartReducer = state => state.cartReducer;

export const selectCartReducerItems = createSelector(
    [selectCartReducer],
    cartReducer => cartReducer.cartItems
);

export const selectCartReducerHidden = createSelector(
    [selectCartReducer],
    cartReducer => cartReducer.hidden
)

export const selectCartReducerItemsCount = createSelector(
    [selectCartReducerItems],
    cartItems =>cartItems.reduce((acc, item) => acc + item.quantity, 0)
)
