import React from 'react';
import { Link } from 'react-router-dom';
import { ReactComponent as Logo } from '../../assets/logo.svg';
import {auth} from '../../firebase/firebase.utils';
import './header.style.scss';
import {connect} from 'react-redux';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDropdown from '../cart-dropdown/cart-dropdown.component';
import clickCartIcon from '../../redux/cart/cart.actions';

const Header = ({currentUser,cartVisible,clickCartIcon}) => (
    <div className='header'>
        <Link to='/' className='logo-container'>
            <Logo className='logo' />
        </Link>
        <div className='options'>
            <Link className='option' to='/shop'>SHOP</Link>
            <Link className='option' to='/contact'>CONTACT</Link>
            {
                currentUser?
                <div className='option' onClick={()=>auth.signOut()}>SIGN OUT</div>
                :<Link className='option' to='/signin'>SIGN IN</Link>
            }
            <CartIcon onClick={cartVisible=>clickCartIcon()}/>
        </div>
        {cartVisible?<CartDropdown/>:null}
    </div>
);

const mapStateToProps = rootReducer =>({
    currentUser:rootReducer.userReducer.currentUser
})

const mapDispatchToProps = dispatch =>({
    clickCartIcon:cartVisible => dispatch(clickCartIcon({type:'TOGGLE_CART_VISIBILITY',payload:cartVisible}))
})

export default connect(mapStateToProps,mapDispatchToProps)(Header);
