import React from 'react';
import './form-input.styles.scss';

const FormInput = ({handleChange,label, ...rest}) =>(
    <div className='group'>
        <input 
        className='form-input'
        onChange={handleChange}
        {...rest}
        />
        {label?
        <label 
        className={`${
            rest.value.length?
            'shrink'
            :''}form-input-label`}>
                {label}
            </label>
        :null}
    </div>
)

export default FormInput;
